mapper.update:
UPDATE WORLD_CITY CN
SET CN.LOGIC_DELETE_FLG = 'removed'
WHERE CN.ID = #{id,jdbcType=NUMBER}

mapper.select:
SELECT COUNT(1)
FROM WORLD_CITY_VIEW CWV
WHERE CWV.NATION = #{nation,jdbcType=NVARCHAR2}

SELECT COUNT(1)
FROM WORLD_CITY CN
WHERE CN.NAME = #{cityName,jdbcType=NVARCHAR2}
